  padding: 60px 0;
  margin-bottom: 60px;
}

.idyll-text-container {
  max-width: 600px;
  margin-top: 0;
  margin-right: 0;
  margin-bottom: 0;
  margin-left: 50px;
}

.section {
  padding: 0 10px;
  margin: 0 auto;
}

.article-header {
  text-align: left;
  padding-left: 50px;
  margin-bottom: 45px;
}

.inset {
  max-width: 400px;
  margin: 0 auto;
}

input {
  cursor: pointer;
}

.relative {
  position: relative;
}
.aside-container {
  position: relative;
  display: block;
}
.aside {
