  margin: 1em auto;
}

h1, h2, h3, h4, h5 {
  border-bottom: none;
}

pre {
  max-width: 960px;
  margin: 2em auto;
}

h1.hed {
  font-size: 4em;
  margin-top: 0;
}
h2.dek {
  font-size: 2em;
  margin: 0.5em auto;
  font-weight: lighter;
}
.article-header {
  background: #222;
  color: white;
  padding-top: 8em;
  padding-bottom: 4em;
  margin-bottom: 4em;
}
.article-header a {
  color: white;
  text-decoration: underline;
}
.idyll-dynamic {
  cursor: ew-resize;
  font-family: monospace;
}
.idyll-display {
  font-family: monospace;
}
img {
  display: block;
  margin: 0 auto;
}

@media all and (max-width: 1000px) {
  .idyll-root {
    max-width: none;
    padding: 0;
  }

  h1.hed {
    font-size: 2em;
  }
  h2.dek {
    font-size: 1em;
  }
}

